import React, { Fragment } from 'react';
import PropTypes from 'prop-types';
import './style.css';
import Button from '../Button';
import Menu from '../Menu';


const Controls = ({
    algorithm,
    onAlgorithmChange,
    onGenerateRandomArray,
    arraySize,
    onArraySizeChange
  }) => {
    return (
      <Fragment>
        <Menu
          placeholder="Sort Algorithm"
          items={[
            'Bubble Sort'
          ]}
          selected={algorithm}
          onSelect={onAlgorithmChange}
        />
  
        <div className="Controls__Size">
          <span>Size</span>
          <Menu
            placeholder="Array Size"
            items={['5', '10', '25', '50', '75', '100']}
            selected={String(arraySize)}
            onSelect={onArraySizeChange}
          />
        </div>

        <Button onClick={insertArray}>Insert
        </Button> <input id="array" type="text" Style="width:100px;height:25px "></input>
        <Button onClick={deleteLastArray}>Delete</Button> 
        <Button onClick={deleteArray}>Clear Array</Button>
        <Button onClick={onGenerateRandomArray}>Randomize</Button>
        
      </Fragment>
    );
  };
  
  Controls.propTypes = {
    algorithm: PropTypes.string,
    onAlgorithmChange: PropTypes.func.isRequired,
    onGenerateRandomArray: PropTypes.func.isRequired,
    arraySize: PropTypes.number,
    onArraySizeChange: PropTypes.func.isRequired,
  };
  
  export default Controls;
