import React from 'react';
import './style.css';

const PseudoCode = ({
  width,
  height,
  val,
  stateA,
  stateB,
  stateC,
  stateD,
  sorted,
  style
}) => {
  let classNames = 'PseudoCode';
  if (sorted) classNames += ' PseudoCode_sorted';
  if (stateD) classNames += ' PseudoCode_stateD';
  else if (stateC) classNames += ' PseudoCode_stateC';
  else if (stateB) classNames += ' PseudoCode_stateB';
  else if (stateA) classNames += ' PseudoCode_stateA';

  let classNames1 = 'PseudoCode';
  if (sorted) classNames1 += ' PseudoCode_sorted';
  if (stateD) classNames1 += ' PseudoCode_stateA';
  else if (stateC) classNames1 += ' PseudoCode_stateB';
  else if (stateB) classNames1 += ' PseudoCode_stateC';
  else if (stateA) classNames1 += ' PseudoCode_stateD';

  /*let BarStyle = { ...style, width: `${width}%`, height: `${height}%` };
  if (stateA || stateB || stateC || stateD) {
    BarStyle['marginRight'] = `${0.3 * width}%`;
    BarStyle['marginLeft'] = `${0.3 * width}% `;
  }*/
  

  return (
    <><div className={classNames}>
          <span className="PseudoCode__Text">{val}</span>
      </div><div className={classNames1}>
              <span className="PseudoCode__Text1">{"Code"}</span>
          </div></>
  );
};

export default PseudoCode;
