import React, { Fragment } from 'react';
import PropTypes from 'prop-types';
import './style.css';
import Button from '../Button';
import Menu from '../Menu';


const Controls = ({
  algorithm,
  onAlgorithmChange,
  onGenerateRandomArray,
  arraySize,
  insertArray,
  insertwithIndex,
  deleteArray,
  deletewholeArray,
  onArraySizeChange,
  }) => {
    return (
      <Fragment>
        <Menu
          placeholder="Sort Algorithm"
          items={[
            'Bubble Sort',
            'Selection Sort',
            'Insertion Sort',
            'Merge Sort',
            'Quick Sort',
            'Heap Sort'
          ]}
          selected={algorithm}
          onSelect={onAlgorithmChange}
        />
  
        <div className="Controls__Size">
          <span>Size</span>
          <Menu
            placeholder="Array Size"
            items={['5', '10', '25', '50', '75', '100']}
            selected={String(arraySize)}
            onSelect={onArraySizeChange}
          />
        </div>

        <Button onClick={insertwithIndex}>insertwithIndex

      </Button> <input id="array2" type="text" Style="width:50px;height:25px color:white;"></input><input id ="index" type="text"Style="width:50px;height:25px "></input>
      
      <Button onClick={deletewholeArray}>Delete_array</Button>
      
      <Button onClick={insertArray}>Insert
      </Button> <input id="array" type="text" Style="width:50px;height:25px "></input>
      
     <Button onClick={deleteArray}>Delete</Button>
      <Button onClick={onGenerateRandomArray}>Randomize</Button>

      </Fragment>
    );
  };
  
  Controls.propTypes = {
    algorithm: PropTypes.string,
  onAlgorithmChange: PropTypes.func.isRequired,
  onGenerateRandomArray: PropTypes.func.isRequired,
  arraySize: PropTypes.number,
  onArraySizeChange: PropTypes.func.isRequired,
  onToggleDarkMode: PropTypes.func.isRequired,
  darkMode: PropTypes.bool,
  insertArray: PropTypes.func.isRequired,
  insertwithIndex: PropTypes.func.isRequired,
  deleteArray: PropTypes.func.isRequired,
  };
  
  export default Controls;
