import React, { Component } from 'react';
import './style.css';

import Controls from '../Controls';
import TopBar from '../TopBar';
import Drawer from '../Drawer';
import SortVisualizer from '../SortVisualizer';

import BubbleSort, {
  BubbleSortKey,
  BubbleSortDesc,
  BubbleSortP
} from '../algorithms/BubbleSort';
import SelectionSort, {
  SelectionSortKey,
  SelectionSortDesc,
  SelectionSortP
} from '../algorithms/SelectionSort';
import InsertionSort, {
  InsertionSortKey,
  InsertionSortDesc,
  InsertionSortP
} from '../algorithms/InsertionSort';
import MergeSort, {
  MergeSortKey,
  MergeSortDesc,
  MergeSortP
} from '../algorithms/MergeSort';
import QuickSort, {
  QuickSortKey,
  QuickSortDesc,
  QuickSortP
} from './algorithms/QuickSort';
import QuickSort3, {
  QuickSort3Key,
  QuickSort3Desc
} from '../algorithms/QuickSort3';
import HeapSort, {
  HeapSortKey,
  HeapSortDesc,
  HeapSortP
} from '../algorithms/HeapSort';
import ShellSort, {
  ShellSortKey,
  ShellSortDesc,
  ShellSortP
} from '../algorithms/ShellSort';
class Khwarizmi extends Component {
  state = {
    array: [],
    arraySize: 10,
    trace: [],
    algorithm: null,
    appDrawerOpen: false
  };

  saveInput = (e) => {
    this.setState({ input: e.target.value });
  };

  ALGORITHM = {
    'Bubble Sort': BubbleSort,
  };

  ALGORITHM_KEY = {
    'Bubble Sort': BubbleSortKey
  };

  ALGORITHM_DESC = {
    'Bubble Sort': BubbleSortDesc
  };

  componentDidMount() {
    this.generateRandomArray();
  }
  

  generateRandomArray = () => {
    // Generate pseudo-random number between 1 and max
    function getRandomInt(max) {
      return Math.floor(Math.random() * Math.floor(max)) + 1;
    }

    // Generate an array of length max
    const array = Array(this.state.arraySize)
      .fill(0)
      .map(() => getRandomInt(this.state.arraySize * 5));

    this.setState(
      {
        array,
        trace: []
      },
      this.createTrace
    );
  };

  deleteArray = (array) => {
    while(array.arraySize){
     array.pop()
   } 
   
   this.setState(
     {
       array:[],
       trace: []
     },
       this.createTrace
     );
 };

 deleteLastArray = () => {
   this.state.array.pop();

   this.setState(
     {
       array:[...this.state.array],
       trace: []
     },
       this.createTrace
     );
 };
 

 insertArray =() => {
   
   let inp = document.getElementById("array").value;
   let val = parseInt(inp)

   this.state.array.push(val);

   this.setState({
     array: [...this.state.array],
     trace: []
   },
     this.createTrace
   );   
 };

  handleAlgorithmChange = (algorithm) => {
    this.setState({ algorithm }, this.generateRandomArray);
  };
  
  handleArraySizeChange = (size) => {
    size = Number(size);
    size = size > 100 ? 100 : size;
    size = size < 0 ? 0 : size;
    this.setState({ arraySize: size }, this.generateRandomArray);
  };

  createTrace = () => {
    const numbers = [...this.state.array];
    const sort = this.ALGORITHM[this.state.algorithm];
    if (sort) {
      const trace = sort(numbers);
      this.setState({ trace });
    }
  };

  toggleDrawer = () => {
    this.setState((prevState) => ({
      drawerOpen: !prevState.drawerOpen
    }));
  };

  render() {
    let theme = `Khwarizmi`;
    if (this.state.drawerOpen) theme += ` Khwarizmi_modal_open`;

    const colorKey = this.ALGORITHM_KEY[this.state.algorithm];
    const desc = this.ALGORITHM_DESC[this.state.algorithm];

    

    const controls = (


      <Controls
        onGenerateRandomArray={this.generateRandomArray}
        algorithm={this.state.algorithm}
        onAlgorithmChange={this.handleAlgorithmChange}
        insertArray={this.insertArray}
        deleteLastArray={this.deleteLastArray}
        deleteArray={this.deleteArray}
        arraySize={this.state.arraySize}
        onArraySizeChange={this.handleArraySizeChange}
      />
    );

    return (
      <div className={theme}>
        <TopBar
          drawerOpen={this.state.drawerOpen}
          toggleDrawer={this.toggleDrawer}
        >
          {controls}
        </TopBar>

        <Drawer
          open={this.state.drawerOpen}
          closeDrawer={this.toggleDrawer}
        >
          {controls}
        </Drawer>

        <main className="Khwarizmi__Body">
          <SortVisualizer
            array={this.state.array}
            trace={this.state.trace}
            colorKey={colorKey}
            desc={desc}
          />
        </main>
      </div>
    );
  }
}

export default Khwarizmi;
