import React from 'react';
import PropTypes from 'prop-types';
import './style.css';

import Bar from '../../celling/Bar';

const getListOfBars = (
  numbers,
  maxNum,
  groupA,
  groupB,
  groupC,
  groupD,
  sortedIndices
) => {
  return numbers.map((num, i) => {
    let width = 100 / numbers.length;
    let height = (num / maxNum) * 100;
    let stateA = groupA.includes(i);
    let stateB = groupB.includes(i);
    let stateC = groupC.includes(i);
    let stateD = groupD.includes(i);
    let sorted = sortedIndices.includes(i);

    let margin =
      i === numbers.length ? '0' : width > 3 ? '0.5rem' : '0.125rem';
    return (
      <Bar
        key={`${i}_${num}`}
        width={width}
        height={height}
        val={width > 4 ? num : null}
        stateA={stateA}
        stateB={stateB}
        stateC={stateC}
        stateD={stateD}
        sorted={sorted}
        style={{ marginRight: `${margin}` }}
      />
    );
  });
};

const SortChart = ({
  numbers,
  maxNum,
  groupA,
  groupB,
  groupC,
  groupD,
  sortedIndices
}) => {
  return (
    <div className="SortChart">
      <div className='SortChartL'>{getListOfBars(
        numbers,
        maxNum,
        groupA,
        groupB,
        groupC,
        groupD,
        sortedIndices
      )}</div>
       <aside className="SortChartR">
       <div className="card post" Style="text-align:left" > 
          <div id="screen" Style="margin: 15px">
                do <br/> 
                <span id="swapped" Style="margin-left:20px">
                swapped = false <br/>
                  </span><br/>
            <span id="change" Style="margin-left:40px" >
              for i = 1 to indexOfLastUnsortedElement-1<br/>
            </span><br/>
            <span id="compare" className="Bar_stateA" Style="margin-left:60px">
              if leftElement > rightElement<br/>
            </span><br/>
            <span id="swap" className="Bar_stateB" Style="margin-left:70px">
                swap(leftElement, rightElement) <br/> </span><br/>
            <span id="swap" className="Bar_sorted" Style="margin-left:70px">
                swapped = true <br/>
            </span><br/>
            <span id="change">while swapped</span>
          </div></div>
         
       </aside>
      
    </div>
  );
};

SortChart.propTypes = {
  numbers: PropTypes.arrayOf(PropTypes.number),
  maxNum: PropTypes.number,
  groupA: PropTypes.arrayOf(PropTypes.number),
  groupB: PropTypes.arrayOf(PropTypes.number),
  groupC: PropTypes.arrayOf(PropTypes.number),
  groupD: PropTypes.arrayOf(PropTypes.number),
  sortedIndices: PropTypes.arrayOf(PropTypes.number)
};

export default SortChart;
