import React from 'react';
import './style.css';

const PseudoCode = ({
  width,
  height,
  val,
  stateA,
  stateB,
  stateC,
  stateD,
  sorted,
  style
}) => {
  let classNames = 'PseudoCode';
  if (sorted) classNames += ' PseudoCode_sorted';
  if (stateD) classNames += ' PseudoCode_stateD';
  else if (stateC) classNames += ' PseudoCode_stateC';
  else if (stateB) classNames += ' PseudoCode_stateB';
  else if (stateA) classNames += ' PseudoCode_stateA';

  /*let BarStyle = { ...style, width: `${width}%`, height: `${height}%` };
  if (stateA || stateB || stateC || stateD) {
    BarStyle['marginRight'] = `${0.3 * width}%`;
    BarStyle['marginLeft'] = `${0.3 * width}% `;
  }*/

  return (
    <div style={BarStyle} className={classNames}>
      <span className="PseudoCode__Text">{val}</span>
    </div>
  );
};

export default PseudoCode;
